                    j++;
                    while(str[j] == 'A'&&str[j] != '\0'){
                        count2++;
                        j++;
                    }
                    if(str[j]!='\0'&&str[j] == 'T'){
                        j++;
                        while(str[j] == 'A'&&str[j] != '\0'){
                            count3++;
                            j++;
                        }
                        if(str[j] != '\0'){//T之后还有除A之外的字符
                            printf("NO\n");
                            break;
                        }
                        else{
                            //满足count1 * count2 == count3且三者不同时为零
                            if((count1 * count2 == count3)&&(count1||count2||count3)){
                                printf("YES\n");
                                break;
                            }
                            else{
                                printf("NO\n");
                                break;
                            }
                        }
                    }
                    else{
                        printf("NO\n");
                        break;
                    }
                }
                else{
                    printf("NO\n");
                    break;
                }
            }
            else{//不满足条件1
                printf("NO\n");
                break;
            }
        }
    }
    return 0;
}

